//функцыя провырки чи введены числа
function enterNumber(str) {
    let arr = str.split('');
    for (let elem of arr) {
        if (elem.charCodeAt() < 49 || elem.charCodeAt() > 57) {
            return true;
        }
    }
    return false;
}

